mapper.select:
select u.ei as mbti, a.emotion, avg(a.amount) as amountAverage
from articles a join users_mbti_view u on(a.user_id = u.user_id)
where a.register_type = #{registerType} and year(a.created_date) = year(now()) and month(a.created_date) = #{month}
group by mbti, a.emotion
union all
select u.ns as mbti, a.emotion, avg(a.amount) as amountAverage
from articles a join users_mbti_view u on(a.user_id = u.user_id)
where a.register_type = #{registerType} and year(a.created_date) = year(now()) and month(a.created_date) = #{month}
group by mbti, a.emotion
union all
select u.tf as mbti, a.emotion, avg(a.amount) as amountAverage
from articles a join users_mbti_view u on(a.user_id = u.user_id)
where a.register_type = #{registerType} and year(a.created_date) = year(now()) and month(a.created_date) = #{month}
group by mbti, a.emotion
union all
select u.pj as mbti, a.emotion, avg(a.amount) as amountAverage
from articles a join users_mbti_view u on(a.user_id = u.user_id)
where a.register_type = #{registerType} and year(a.created_date) = year(now()) and month(a.created_date) = #{month}
group by mbti, a.emotion
